settings.custom_css:
selector .aux-head-before, selector .aux-head-highlight{
background: var(--e-global-color-secondary);
background: -moz-linear-gradient(271deg, var(--e-global-color-secondary) 0%, var(--e-global-color-primary) 100%);
background: -webkit-linear-gradient(271deg, var(--e-global-color-secondary) 0%, var(--e-global-color-primary) 100%);
background: linear-gradient(271deg, var(--e-global-color-secondary) 0%, var(--e-global-color-primary) 100%);
 -webkit-background-clip: text;
 -webkit-text-fill-color: transparent;
}
selector .aux-modern-heading-description{
    margin-right: auto;
    margin-left: auto;
}

selector .entry-title:after{
    content: '';
    width: 30px;
    height: 6px;
    background: var(--e-global-color-secondary);
    background: linear-gradient(277deg, var(--e-global-color-secondary) 4%, var(--e-global-color-primary) 84%);
    position: absolute;
    border-radius:8px ;
    left:-40px;
    top: 15px;
}
selector .entry-main{
    box-shadow: 0 10px 35px rgba(0,0,0,0.29);
    border-radius:10px;
    margin: auto;
    background: #fff;
    position: absolute;
    bottom: 51px;
    max-width: 90%;
    left: 0;
    right: 0;
    height: 110px;
       transition:all 1800ms cubic-bezier(0.19, 1, 0.22, 1);
}
@media only screen and (max-width: 1487px)and (min-width: 991px) {
    selector .entry-main{
    height: 130px;
       transition:all 1800ms cubic-bezier(0.19, 1, 0.22, 1);
}
}
selector .aux-isotope-ready {
    overflow: visible;
}
@media only screen and (max-width: 1406px)and (min-width: 991px) {
selector  .entry-title a{
   font-size: 20px!important;
   line-height: 30px!important;
} 
selector .entry-tax {
   font-size: 13px;
}
selector .entry-main {
    padding: 6% 0% 1% 16% !important;
}
}
selector .entry-info{
    margin-bottom: 11px;
}
selector .aux-media-image{
box-shadow: 0 3px 35px rgba(0,0,0,0.19);
}
selector .aux-iso-item .hentry:hover .entry-title a, selector .aux-iso-item .hentry:hover  .entry-tax a{
    color: #fff!important;
}
selector .aux-iso-item .hentry:hover  .entry-main{
    background: var(--e-global-color-secondary);
    height: 80%;
    background: linear-gradient(180deg, var(--e-global-color-secondary) 4%, var(--e-global-color-primary) 84%);
     transition:all 1800ms cubic-bezier(0.19, 1, 0.22, 1);
    padding-top: 7%!important;
    opacity: 0.95;
} 
selector .aux-overlay-content{
    bottom: 0;
    right: 10%;
    transform: translateY(-15%);
    top: auto;
    text-align: right;
    padding-right: 8px;
}
selector .aux-frame-darken::after {
    opacity: 0;
}
selector .aux-iso-item .hentry:hover  .entry-title:after{
    background: #fff!important;
}
selector .aux-svg-arrow{
    background-image: none;
}
selector .aux-hover-circle-plus.aux-delay-2x{
    display: none!important;
}
selector .aux-svg-arrow:after{
    content: '\e682';
    font-family: "auxin-front" !important;
    font-style: normal !important;
    font-weight: normal !important;
    font-variant: normal !important;
    text-transform: none !important;
    speak: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    color: #fff;
    font-size: 30px;
}
selector  .aux-overlay::after{
    background-color: transparent !important;
}
selector .aux-white{
    border: none!important;  
    box-shadow: none;
}
selector .entry-media{
    border-radius: 13px;
}
